mapper.select:
SELECT A.CUST_NO AS CUST_NO /* 고객번호 */ , CASE WHEN LENGTH(A.CUST_NM) = 2 THEN SUBSTR(A.CUST_NM, 1, 1) || '*' /* 고객명 */ ELSE /* 2글자면 마지막 글자만 */ SUBSTR(A.CUST_NM, 1, 1) || /* 3글자이상시 첫,마지막을 제외한 */ LPAD('*', LENGTH(A.CUST_NM)-2, '*') || /* 중간부분을 마스킹처리 */ SUBSTR(A.CUST_NM, LENGTH(A.CUST_NM), 1) END AS CUST_NM , SUBSTR(A.MBL_NO,1,3) || '-' || /* 휴대폰번호 */ LPAD('*', LENGTH(A.MBL_NO)-7, '*') || '-' || /* 중간 번호를 마스킹 처리 */ SUBSTR(A.MBL_NO, -4) AS MBL_NO , CASE A.CUST_SS_CD /* 고객상태코드 */ WHEN '10' THEN '정상' /* 코드가 10이면 정상 */ WHEN '80' THEN '중지' /* 코드가 80이면 중지 */ WHEN '90' THEN '해지' /* 코드가 90이면 해지 */ END AS CUST_SS_CD , TO_CHAR(TO_DATE(A.JS_DT), 'YYYY-MM-DD') AS JS_DT /* 가입일자 */ , B.PRT_NM AS PRT_NM /* 가입매장 */ , TO_CHAR(TO_DATE(A.FST_REG_DT), 'YYYY-MM-DD') || '/' || C.USER_NM AS USER_NM /* 등록일자/등록자 */ , TO_CHAR(TO_DATE(A.LST_UPD_DT), 'YYYY-MM-DD HH24:MI:SS') AS LST_UPD_DT /* 수정일자 */ FROM CS_CUST01_MT A INNER JOIN MA_PRT_MT B /* 고객관리테이블과 거래처관리 테이블 JOIN */ ON A.JN_PRT_CD = B.PRT_CD /* 가입매장코드 = 거래처코드 */ INNER JOIN MA_USER_MT C /* 고객관리테이블과 사용자 테이블 JOIN */ ON A.FST_USER_ID = C.USER_ID /* 등록자아이디 = 사용자아이디 */ WHERE 1=1 AND ( TO_DATE(A.JS_DT, 'YYYY-MM-DD') BETWEEN TO_DATE(#{SDATE}, 'YYYY-MM-DD') AND TO_DATE(#{EDATE}, 'YYYY-MM-DD') ) /* 사용자구분코드가 2가 아니고 매장번호가 공백이라면(초기조건) */ AND A.JN_PRT_CD = #{SE_PRT_CD} /* 세션에 저장된 매장코드와 일치하는 값 */
/* 매장검색란이 공백이 아니라면(검색조건) */ AND ( A.JN_PRT_CD LIKE '%' || #{PRT_CD_NM} || '%' /* 매장검색란에 적힌 값과 매장코드가 부분일치 하는 값 */ OR B.PRT_NM LIKE '%' || #{PRT_CD_NM} || '%' /* 매장검색란에 적힌 값과 매장명이 부분일치 하는 값 */ )
/* 고객번호가 기본값이 아니라면 */ AND A.CUST_NO LIKE '%' || #{IN_CUST_NO} || '%' /* 고객번호검색란에 적힌 값과 고객번호가 부분일치 하는 값 */
/* 고객상태코드가 기본값이 아니라면 */ AND A.CUST_SS_CD = #{CUST_SS_CD} /* 고객상태코드가 일치하는 값 */
ORDER BY CUST_NO
